import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import DisplayMovie from './DisplayMovie.js'

class App extends Component {
  constructor() {
    super();
    this.state = {
      userInput: '',
      movieId: [],
      movies: [],
      stats: []
    }
  }

  _isMounted = false

  getMovie() {
    const movieInput = this.state.userInput;

      axios.get(
          'https://cors-anywhere.herokuapp.com/https://www.doesthedogdie.com/search?',
          {
            method: 'GET',
            dataType: 'json',
            headers: {
              'X-API-KEY': 'e39ba046c39413e2c04848ae44e80a73',
              Accept: 'application/json'
            },
            params: {
              q: movieInput
            }
          }
        )
        .then(response => {
          response = response.data.items
          const id = response.map(movies => {
            return movies.id
          })
          this.setState ({
            movieId:id
          })
          const inputId = this.state.movieId;


          inputId.map(async (value) => {
            try {
              const url = `https://cors-anywhere.herokuapp.com/https://www.doesthedogdie.com/media/${value}`
              const result = await axios.get(`${url}`, {
                method: 'GET',
                dataType: 'json',
                headers: {
                  'X-API-KEY': 'e39ba046c39413e2c04848ae44e80a73',
                  Accept: 'application/json',
                },
              })
              const movieResponse = result.data.item.name;
              const movieObj = Object.assign(movieResponse)

              console.log(movieObj);


              console.log(movieResponse);
              const itemStats = Object.entries(result.data.topicItemStats[1]);
              if (this._isMounted) {
                this.setState({
                  movies: movieResponse,
                  stats: itemStats
                })

              }

              console.log(this.state.movies)
              console.log(this.state.stats)
            } catch(err) {
              console.log(err.message);
            }
          })
        })
  }


  handleChange = (event) => {
    this.setState({
      userInput: event.target.value
    })
  }

  handleClick = (event) => {
    event.preventDefault();
    this.setState({
      userInput: ''
    })
    this.getMovie()
  }

  componentDidMount() {
    this._isMounted = true
  }
  componentWillUnmount() {
    this._isMounted = false
  }

  render() {
    return (
    <div className="App">

      <form action="">
        <input 
          onChange={this.handleChange} 
          type='text' 
          placeholder='Type Movie Here' 
          value={this.state.userInput}
          name='search' />
        <button onClick={this.handleClick}>Search!</button>
      </form>
{/* 
      <div className="display">
          { this.state.movies.map( (movie) => {
            return (
              <DisplayMovie name={movie[1]} />
            )
          }) }
      </div> */}
    </div>
    );
  }

}

export default App;
