import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

class App extends Component {
  constructor() {
    super();
    this.state = {
      userInput: '',
      movieId: [],
      movies: [],
      stats: []
    }

  }


  componentDidMount() {
    const movieInput = this.state.userInput;

    axios.get(
        'https://cors-anywhere.herokuapp.com/https://www.doesthedogdie.com/search?',
        {
          method: 'GET',
          dataType: 'json',
          headers: {
            'X-API-KEY': 'e39ba046c39413e2c04848ae44e80a73',
            Accept: 'application/json'
          },
          params: {
            q: movieInput
          }
        }
      )
      .then(response => {
        response = response.data.items
        const id = response.map(movies => {
          return movies.id
        })
        this.setState ({
          movieId:id
        })

        const inputId = this.state.movieId;
        inputId.map((value) => {
            const url = `https://cors-anywhere.herokuapp.com/https://www.doesthedogdie.com/media/${value}`
            axios.get(`${url}`, {
              method: 'GET',
              dataType: 'json',
              headers: {
                'X-API-KEY': 'e39ba046c39413e2c04848ae44e80a73',
                Accept: 'application/json',
              },
            })
            const movieResponse = value.data.item.name;
            const itemStats = value.data.topicItemStats[0];
            this.setState({
              movies: movieResponse,
              stats: itemStats
            })
            console.log(this.state.movies)
            console.log(this.state.stats)
        })
      })
  }


  handleChange = (event) => {
    this.setState({
      userInput: event.target.value
    })
  }

  handleClick = (event) => {
    event.preventDefault();
    this.setState({
      userInput: ''
    })
    this.componentDidMount()
  }

  render() {
    return (
    <div className="App">

      <form action="">
        <input 
          onChange={this.handleChange} 
          type='text' 
          placeholder='Type Movie Here' 
          value={this.state.userInput}
          name='search' />
        <button onClick={this.handleClick}>Search!</button>
      </form>

      {/* <div className="display">
        { this.state.movies.map( (movie) => {
          return (
            <p>{movie}</p>
          )
        }) }
      </div> */}
    </div>
    );
  }

}

export default App;
