import React, { Component } from 'react';
import './App.css';
import axios from 'axios';


class App extends Component {
  constructor() {
    super();
    this.state = {
      userInput: '',
      movieId: [],
      movies: [],
      stats: [],
      allMovies: []
    }
  }

  _isMounted = false

  componentDidMount() {
    const movieInput = this.state.userInput;

      axios.get(
          'https://cors-anywhere.herokuapp.com/https://www.doesthedogdie.com/search?',
          {
            method: 'GET',
            dataType: 'json',
            headers: {
              'X-API-KEY': 'e39ba046c39413e2c04848ae44e80a73',
              Accept: 'application/json'
            },
            params: {
              q: movieInput
            }
          }
        )
        .then(response => {
          response = response.data.items
          console.log(response)
          const ids = response.map(movies => {
            return movies.id
          })

          this.setState ({
            movieId:ids
          })

          const movieName = response.map(names => {
            return names.name
          })
          const movieNamesArray = [];
          response.forEach(movie => {
            movieNamesArray.push({
              name: movie.name
            })
          })
          console.log(movieNamesArray)

          this.setState({ allMovies: movieNamesArray })

          this.setState ({
            movies: movieName
          })
          this.getStats()
          this.getPoster()
        })
  }

  getStats() {
    const inputId = this.state.movieId
    inputId.map(async value => {
        try {
          const url = `https://cors-anywhere.herokuapp.com/https://www.doesthedogdie.com/media/${value}`
          const result = await axios.get(`${url}`, {
            method: 'GET',
            dataType: 'json',
            headers: {
              'X-API-KEY': 'e39ba046c39413e2c04848ae44e80a73',
              Accept: 'application/json'
            }
          })
          const newResult = Object.values(result.data.topicItemStats[0]);
          console.log(newResult);
          console.log(newResult[0], newResult[1])

          if (newResult[0] > newResult[1]) {
            this.setState(prevState => ({
              stats: [...prevState.stats, 'A Dog Dies'] 
            }))

          } else if (newResult[0] < newResult[1]) {
            this.setState(prevState => ({
              stats: [...prevState.stats, 'A Dog Doesnt die']
            }))


          } else {
              this.setState(prevState => ({
                stats: [...prevState.stats, 'Take a risk']
              }))

          }
          console.log(this.state.stats)

        } catch (err) {
          console.log(err.message)
        }
      })
  }

    getPoster() {

    const selectedMovie = this.state.movies
    const selectedMovies = this.state.allMovies;
    selectedMovies.forEach(movie => {
      //movie.name is going to be your search query.
      // movie.poster = whatever your poster thing is
    })

    selectedMovie.map(async select => {
      try {
        const url = `https://api.themoviedb.org/3/search/movie?api_key=8d57b009677e25546dc89ff6368e4fbe&query=${select}`
        const poster = await axios.get(`${url}`, {
        method: 'GET',
        dataType: 'jsonp'
        })
        const resultPoster = poster.data.results
        const moviePoster = resultPoster.map(post => {
          return post.poster_path
        })
        console.log(moviePoster[0])

        const posterUrl = `http://image.tmdb.org/t/p/w500${moviePoster[0]}`

        this.setState({
          posters: posterUrl
        })
        console.log(this.state.posters)

      } catch (err) {
        console.log(err)
      }
    })
  }


  handleChange = (event) => {
    this.setState({
      userInput: event.target.value
    })
  }

  handleClick = (event) => {
    event.preventDefault();
    this.setState({
      userInput: ''
    })
    this.componentDidMount()

  }


  componentWillUnmount() {
    this._isMounted = false
  }

  render() {
    return (
    <div className="App">

      <form action="">
        <input 
          onChange={this.handleChange} 
          type='text' 
          placeholder='Type Movie Here' 
          value={this.state.userInput}
          name='search' />
        <button onClick={this.handleClick}>Search!</button>
      </form>

      <div className="display">

        <div className="movie">
           { this.state.movies.map( (movie) => {
            return (
              <p>{movie}</p>
            )
          })}
        </div>

        <div className="dog">
          { this.state.stats.map( (dog) => {
          return (

            <p>{dog}</p>

            )
          })}
        </div>
      </div>
    </div>
    );
  }

}

export default App;
