import React, { Component } from 'react';
import './App.css';
import axios from 'axios';


class App extends Component {
  constructor() {
    super();
    this.state = {
      userInput: '',
      movieId: [],
      movies: [],
      stats: []
    }
  }

  _isMounted = false

  componentDidMount() {
    const movieInput = this.state.userInput;

      axios.get(
          'https://cors-anywhere.herokuapp.com/https://www.doesthedogdie.com/search?',
          {
            method: 'GET',
            dataType: 'json',
            headers: {
              'X-API-KEY': 'e39ba046c39413e2c04848ae44e80a73',
              Accept: 'application/json'
            },
            params: {
              q: movieInput
            }
          }
        )
        .then(response => {
          response = response.data.items
          console.log(response)
          const id = response.map(movies => {
            return movies.id
          })
          this.setState ({
            movieId:id
          })

          const movieName = response.map(names => {
            return names.name
          })
          this.setState ({
            movies: movieName
          })
          this.getStats()
        })
  }

  getStats() {
    const inputId = this.state.movieId
    inputId.map(async value => {
        try {
          const url = `https://cors-anywhere.herokuapp.com/https://www.doesthedogdie.com/media/${value}`
          const result = await axios.get(`${url}`, {
            method: 'GET',
            dataType: 'json',
            headers: {
              'X-API-KEY': 'e39ba046c39413e2c04848ae44e80a73',
              Accept: 'application/json'
            }
          })
          const newResult = Object.values(result.data.topicItemStats[0]);

          console.log(newResult[0], newResult[1])

          if (newResult[0] > newResult[1]) {
            this.setState(prevState => ({
              stats: prevState.stats + ['Dog Dies']
            }))
          } else if (newResult[0] < newResult[1]) {
            this.setState(prevState => ({
              stats: prevState.stats + ['Dog Doesnt']
            }))

          } else {
            this.setState(prevState => ({
              stats: prevState.stats + ['Take a risk']
            }))

          }
          console.log(this.state.stats)

        } catch (err) {
          console.log(err.message)
        }
      })
  }


  handleChange = (event) => {
    this.setState({
      userInput: event.target.value
    })
  }

  handleClick = (event) => {
    event.preventDefault();
    this.setState({
      userInput: ''
    })
    this.componentDidMount()

  }

  // componentDidMount() {
  //   this._isMounted = true
  // }
  componentWillUnmount() {
    this._isMounted = false
  }

  render() {
    return (
    <div className="App">

      <form action="">
        <input 
          onChange={this.handleChange} 
          type='text' 
          placeholder='Type Movie Here' 
          value={this.state.userInput}
          name='search' />
        <button onClick={this.handleClick}>Search!</button>
      </form>

      <div className="display">
           { this.state.movies.map( (movie) => {
            return (
              <div>
              <p>{movie}</p>
              <p>{this.state.stats}</p>
              </div>
            )
          })}


      </div>
    </div>
    );
  }

}

export default App;
